import Link from "next/link";
import Nav from "./Nav";
import styled from "styled-components";

//here we create a const where we will create like a "component" for style, ,remember the materialUi where the style is at top? similar. Easy peasy Lina.Also, use `` to wrap .. the style.tag and the values, then use this const name where you want the styles to be apply to.
const Logo = styled.h1`//^^ will apply just to this page h1, and will have decendents selector inside of each component
  font-size: 4rem;
  margin-left: 2rem;
  position: relative;
  z-index: 2;
  transform: skew(-7deg);
  background: red;


  a {
      color: white;
      text-decoration: none;
      text-transform: uppercase;
      padding: 0.5rem 1rem;

  }
`;

const HeaderStyles = styled.header `
.bar{
    border-bottom: 10px solid var(--black, black);


}
.sub-bar{

}
`
export default function Header() {
  return (
    <HeaderStyles>
      <div className="bar">
        <Logo>
          <Link href="/">Stick Fits</Link>
        </Logo>
      </div>
      <div className="sub-bar">
        <p>Search</p>
      </div>
      <Nav />
    </HeaderStyles>
  );
}
